import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

class ContatcsModal extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
    }
  }

  render() {
    return (

      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
      >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter" 
                style={{
                    fontSize: '1.1rem',
                    fontWeight: '900',
                    color:'#777'
                }}>
                Contatti
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <div style={{
              minWidth: '350px'
            }}>
              <div style={{
                display: 'block',
                maxWidth: '100%'
                }}>
                <label style={{
                  display: 'block',
                  margin: '0 0 .5rem 0',
                  color: '#777',
                  fontSize: '1.1rem',
                  fontWeight: '400'
                }}>
                    Se vuoi contattarci per segnalarci un errore, richiedere una collaborazione o contribuire allo sviluppo del sito non esistare a contattarci.
                    Qua sotto trovi le email degli sviluppatori alle quali puoi scrivere:
                    <ul>
                      <li><a href="mailto:banfi.michele@studenti.salesianisesto.it"></a>banfi.michele@studenti.salesianisesto.it</li>
                      <li>b</li>
                      <li>c</li>
                    </ul>
                </label>
              </div>
            </div>
          </div>
        </Modal.Body>
      </Modal>

    );
  }

}
export default ContatcsModal;
