import React from 'react';
import Nav from './Nav.js';
import Maps from './Maps.js';
import SmoothScroll from 'smooth-scroll';

class Header extends React.Component {


  constructor(props) {
    super(props);
    this.state = {
      components: "header"
    }
  }


  render() {

    const scroll = new SmoothScroll('a[href*="#"]', {
      speed: 300,
      ease: 'easeInOutQuad'
    });

    const maps = (<Maps />);
    const header = (<header>
        <Nav router={this.props.router} login={this.props.login} user={this.props.user}/>
        <div className="cover">
          <div className="container">
            <div className="flex-wrapper">
              <div className="text-wrapper">
                <span className="brand">ABOUTMI</span>
                <h1 className="h1-title-dark">Esplora Milano tra Bar, Pub, Ristoranti e Discoteche.</h1>
                <a href="#site"><div className="custom-btn" >Visita il sito</div></a>
              </div>
              <div className="card-wrapper">
                <div className="card">
                  <p>Scopri gli eventi pi√π adatti a te nella tua zona!</p>
                  <div className="custom-btn" onClick={() => {this.props.router("maps");}}>Vedi mappa</div>
                </div>
              </div>
            </div>
          </div>
          <div className="scroll-wrapper">
            <div className="scroll-btn">
              <div className="line-wrapper">
                <span className="line"></span>
              </div>
              <span>Scroll</span>
              <span>down</span>
            </div>
          </div>
        </div>
      </header>); 

    switch (this.state.components) {
      case "header":
        return(header);
      case "maps":
        return(maps);
      default:
        return(header);
    }
  }

}
export default Header;
