import React, { useState } from 'react';
import logo from '../icons/logo.png';
import Header from './Header.js';
import Modal from 'react-bootstrap/Modal';

class Nav extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        components: "main",
        showModal: false
    }
  }


  render() {

    const hideModal = () => {
      this.setState({showModal: false});
    }

    console.log(this.props);

      return(
      <div>
      <nav>
        <div className="nav-container">
          <div className="nav-logo">
            <img className="nav-image" src={logo} alt="Logo AboutMI" />
          </div>
          <div className="menu-icon">
            <span className="bar one"></span>
            <span className="bar two"></span>
            <span className="bar three"></span>
          </div>
          <ul className="nav-items">
            <li><a className="nav-link" onClick={() => {}}>Cosa facciamo</a></li>
            <li><a className="nav-link" onClick={() => {}}>Contatti</a></li>
            <li><a className="nav-link" onClick={() => {this.props.router("signup");}}>Lavora con noi </a></li>
            <li><a className="nav-link btn-login" onClick={()=>{this.setState({showModal: true})}}>Login</a></li>
          </ul>
        </div>
      </nav >
      <Modal show={this.state.showModal} onHide={hideModal}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="modal">
            <label>Nome utente: </label>
            <input placeholder="Scrivi il tuo nome utente"></input>
            <label>Password: </label>
            <input placeholder="Scrivi qui la tua password"></input>
            <div className="btn-wrapper">
              <div className="btn">Accedi</div>
              <div className="btn-text">Registrati</div>
            </div>
            </div>
        </Modal.Body>
      </Modal>   
      </div>
      );
  }
  }

export default Nav;
