import React, { useState } from 'react';
import logo from '../icons/logo.png';
import Header from './Header.js';
import Modal from 'react-bootstrap/Modal';
import ModalHeader from 'react-bootstrap/ModalHeader';
import ModalTitle from 'react-bootstrap/ModalTitle';
import ModalBody from 'react-bootstrap/ModalBody';


class Nav extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        components: "main",
        showModal: false
    }
  }


  render() {

    const hideModal = () => {
      this.setState({showModal: false});
    }

    const [show, setShow] = React.useState(0);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return(
      <div>
        <nav>
          <div className="nav-container">
            <div className="nav-logo">
              <img className="nav-image" src={logo} alt="Logo AboutMI" />
            </div>
            <div className="menu-icon">
              <span className="bar one"></span>
              <span className="bar two"></span>
              <span className="bar three"></span>
            </div>
            <ul className="nav-items">
              <li><a className="nav-link" onClick={() => {}}>Cosa facciamo</a></li>
              <li><a className="nav-link" onClick={() => {}}>Contatti</a></li>
              <li><a className="nav-link" onClick={() => {this.props.router("signup");}}>Lavora con noi </a></li>
              <li><a className="nav-link btn-login" variant="primary" onClick={handleShow}>Login</a></li>
            </ul>
          </div>
        </nav >
        <Modal
          show={this.state.showModal}
          onHide={hideModal}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"
          >
          <ModalHeader closeButton>
            <ModalTitle id="example-custom-modal-styling-title">
              Custom Modal Styling
            </ModalTitle>
          </ModalHeader>
          <ModalBody>
            <p>
            Prova Modal
            </p>
          </ModalBody>
        </Modal>
      </div>
      );
  }
  }

export default Nav;
