import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

class ReviewModal extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
    }
  }

  render() {
    return (

      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter"> {this.props.placeName} </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <div style={{
              minWidth: '350px'
            }}>
              <div style={{
                display: 'block',
                maxWidth: '350px'
                }}>
                <label style={{
                  display: 'block',
                  margin: '0 0 .5rem 0',
                  color: '#777',
                  fontSize: '1.1rem',
                  fontWeight: '400'
                }}>Recensioni dei Clienti</label>
              </div>
            </div>
            <div style={{
              width: '100%',
              height: '100%',
              padding: '0 0 0 50px'
            }}>
              <p id="login-error">Compila i seguenti campi per accedere al sito e poter commentare.</p>
            </div>
          </div>
        </Modal.Body>
        <Modal.Body>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            width: '100%',
            flexDirection: 'column'
          }}>
            <div style={{
            }}>
              <div style={{
                  display: 'block',
                  width: '100%',
                  border: 'solid',
                  borderColor: '#777',
                  padding: '10px',
                  borderRadius: '8px'
                  }}>
                <label style={{
                  display: 'block',
                  margin: '0 0 .5rem 0',
                  color: '#777',
                  fontSize: '1.1rem',
                  fontWeight: '400'
                }}>Scrivi la tua recensione: </label>
                <textarea style={{
                  width: '100%',
                  maxHeight: '250px',
                  minHeight: '50px',
                  margin: '0 0 1.5rem 0',
                  padding: '15px',
                  border: 'none',
                  borderRadius: '8px',
                  outline: 'none',
                  caretColor: '#004ffc',
                  background: '#f1f1f1',
                  fontSize: '.9rem'
                  }} placeholder="Scrivi il tuo nome utente" onChange={e=>this.setState({username: e.target.value})}></textarea>
                  <div className="custom-btn" onClick={()=>{}}>Pubblica Recensione</div>
              </div>
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
            <p>Per pubblicare una recesione devi prima possedere un account!</p>
        </Modal.Footer>
      </Modal>

    );
  }

}
export default ReviewModal;
