import React from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from 'mapbox-gl';

mapboxgl.accessToken = 'pk.eyJ1IjoiZ2lhbmx1MDEiLCJhIjoiY2s1ejQ0a2gyMDY5NjNtcWp5cGF4Y21wMiJ9.S2-22wqQvv8B0aiya-Mh7A';


class Maps extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        lng: 45.466944,
        lat: 9.19,
        zoom: 13
      }
      var map = new mapboxgl.Map({
        container: 'root',
        style: 'mapbox://styles/mapbox/streets-v11'
      });
    }
      //const Milan = transform([45.466944, 9.19], 'ESPG:4326', 'ESPG:4357');

      componentDidMount(){
        const map = new mapboxgl.Map({
          container: this.mapContainer,
          style: 'mapbox://styles/mapbox/streets-v11',
          center: [this.state.lng, this.state.lat],
          zoom: this.state.zoom
        });
      }

    render() {
      return (
        <div>
        <div style={{display: 'inline-block', position: 'absolute', top: 0, left: 0, margin: '12px', backgroundColor: '#404040', color: '#ffffff', zIndex: 1, padding: '6px', fontWeight: 'bold'}}>
        <div>Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom: {this.state.zoom}</div>
        </div>
        <div ref={el => this.mapContainer = el} style={{ position: 'absolute', top: 0, right: 0, left: 0, bottom: 0 }}></div>
        </div>
      );
    }
  }
export default Maps;
