{if (markerState.state){
                    <Marker
                      coordinates={[-0.2416815, 51.5285582]}
                      anchor="bottom">
                    </Marker>
                    //onClick={this.onMarkerClick.bind(this, feature.geometry.coordinates)}>
                  }}*/
